import React from 'react';
import './Navbar.css';
import { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';


function Navbar() {
    const [showSidebar, setShowSidebar] = useState(false);
    function toggleSidebar(){
        setShowSidebar(!setShowSidebar);
    }
  return (
    <nav className='navbar navbar-expand-lg navbar-dark bg-transparent'>
        <div className='container'>
            {/* logo */}
            <a className='navbar-brand fs-4' href='#home'><img src='skyflare.png' alt='SkyFlare' className='sky'></img></a>
           {/* togglebutton */}
           <button className='navbar-toggler shadow-none border-0'  type='button' onClick={toggleSidebar}>
            <span className='navbar-toggler-icon'></span>
           </button>
           {/* sidebar */}
           <div className={`sidebar offcanvas offcanvas-start ${showSidebar ? 'show': ''}`} tapIndex='-1' id='offcanvasNavbar'aria-labelledby='offcanvasNavbarLabel'></div>

           {/* sidebar header */}
           <div className='offcanvas-header'>

           </div>


        </div>

    </nav>
  )
}

export default Navbar
